import { makeRequest } from "./api";
import mongoose from "mongoose";
import Post from "../models/Post.js";
import Comment from "../models/comment.model.js";
import Like from "../models/like.model.js";

export function createComment({ postId, message, parentId }) {
  const token = localStorage.getItem('token');  // Get the token from localStorage

  if (!token) {
    return Promise.reject('User is not authenticated');
  }

  return makeRequest(`/posts/${postId}/comments`, {
    method: "POST",
    headers: {
      'Authorization': `Bearer ${token}`,  // Send the token in the Authorization header
      'Content-Type': 'application/json',  // Ensure the request body is JSON
    },
    data: { message, parentId },  // Do not pass the user explicitly, as the backend extracts it from the token
  });
}



export function updateComment({ message, id }) {
  const token = localStorage.getItem('token');
  return makeRequest(`/comments/${id}`, {
    method: "PUT",
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
    data: { message },
  });
}

export function deleteComment({ id }) {
  const token = localStorage.getItem('token');
  return makeRequest(`/comments/${id}`, {
    method: "DELETE",
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
  });
}


export function toggleCommentLike({ id: commentId }) {
  const token = localStorage.getItem('token');
  return makeRequest(`/likes/toggle`, {
    method: "POST",
    headers: {
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
    data: { commentId },
  });
}

export const getPostWithComments = async (req, res) => {
    const postId = req.params.postId;
    const userId = req.user._id;

    try {
        console.log("ðŸ“Œ Looking for post ID:", req.params.postId);
        console.log("ðŸ”Ž req.user:", req.user);       // Should include `id` and `_id`
        console.log("ðŸ§  req.user._id:", req.user._id);
        console.log("ðŸ§  req.user.id:", req.user.id); // Might be undefined
        const post = await Post.findById(postId).populate('author', 'username');
        console.log("Populated author:", post.author);
        console.log("ðŸ“Œ Post found:", post);
        if (!post) return res.status(404).json({ error: 'Post not found' });

        const comments = await Comment.find({ postId: new mongoose.Types.ObjectId(postId) })
                .sort({ createdAt: 1 })
                .populate('user', 'username');
        console.log("Hi we are comments: " , comments)
        const formattedComments = await Promise.all(
            comments.filter(c => c.user).map(async comment => {
                const likeCount = await Like.countDocuments({ commentId: comment._id });
                const likedByMe = userId
                    ? await Like.exists({ commentId: comment._id, user: userId })
                    : false;

                return {
                    ...comment.toObject(),
                    likeCount,
                    likedByMe: !!likedByMe,
                    user: {
                        id: comment.user._id,
                        name: comment.user.username,
                    },
                };
            })
        );

        return res.status(200).json({
            post: {
                ...post.toObject(),
                author: {
                    id: post.author._id,
                    name: post.author.username,
                },
            },
            comments: formattedComments
        });

    } catch (error) {
        console.log("ðŸ”¥ req.user:", req.user);
        console.error("âŒ Fetch post with comments error:", error);
        return res.status(500).json({ error: "Failed to fetch post and comments" });
    }
};


// This function makes a request to get the current user data based on the JWT token.
export function getCurrentUser(token) {
  return fetch('/api/auth/user', {
    method: 'GET',
    headers: {
      'Authorization': `Bearer ${token}`,  // Pass token in Authorization header
    },
  })
    .then(response => response.json())
    .then(data => data.user)  // Assume the response contains a 'user' object
    .catch(error => {
      console.error('Error fetching current user:', error);
      throw error;
    });
}
