import React, { useState } from "react";
import CommentsSection from "./CommentsSection";

// Optional: Pass in your inline icons instead of re-declaring
const Icon = {
  Heart: (props) => (
    <svg viewBox="0 0 24 24" fill="currentColor" className="w-4 h-4" {...props}>
      <path d="M12 21s-7.2-4.35-9.6-8.4C.5 9.2 2.3 5.5 6 5.5c2 0 3.2 1 4 2 0.8-1 2-2 4-2 3.7 0 5.5 3.7 3.6 7.1C19.2 16.65 12 21 12 21z" />
    </svg>
  ),
  Message: (props) => (
    <svg viewBox="0 0 24 24" fill="none" className="w-4 h-4" stroke="currentColor" strokeWidth="2" {...props}>
      <path d="M21 15a4 4 0 0 1-4 4H8l-5 4V7a4 4 0 0 1 4-4h10a4 4 0 0 1 4 4v8z" />
    </svg>
  ),
};

export default function PostCard({
  post,
  isDarkMode,
  onLikeToggle,   // (postId, liked) => {}
}) {
  const [openComments, setOpenComments] = useState(false);

  const toggleComment = () => setOpenComments((prev) => !prev);

  const likeInfo = {
    liked: post.likedByMe ?? false,
    count: post.likeCount ?? 0
  };

  const attachments = Array.isArray(post.attachments) ? post.attachments : [];

  return (
    <div
      className={`p-6 rounded-xl shadow border ${
        isDarkMode ? "bg-gray-800 border-gray-700" : "bg-white border-gray-200"
      }`}
    >
      <h3 className="text-xl font-bold mb-2">{post.title}</h3>
      <p className="whitespace-pre-wrap">{post.content || post.body}</p>

      {attachments.length > 0 && (
        <div className="mt-4 grid grid-cols-1 sm:grid-cols-2 gap-3">
          {attachments.map((att, i) => (
            <a
              key={att.publicId || i}
              href={att.url}
              target="_blank"
              rel="noreferrer"
              className="block group"
            >
              <div className="relative w-full overflow-hidden rounded-xl border aspect-[4/3] bg-gray-100 dark:bg-gray-800">
                <img
                  src={att.url}
                  alt={`attachment ${i + 1}`}
                  className="absolute inset-0 w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                  onError={(e) => {
                    e.currentTarget.style.display = "none";
                  }}
                />
              </div>
            </a>
          ))}
        </div>
      )}

      {/* Action bar */}
      <div className="mt-4 flex flex-wrap gap-3 text-sm">
        <button
          onClick={() => onLikeToggle?.(post._id || post.id, likeInfo.liked)}
          className={`inline-flex items-center gap-2 px-3 py-1.5 rounded-lg border transition ${
            likeInfo.liked
              ? "bg-blue-600 text-white border-blue-600"
              : isDarkMode
              ? "border-gray-600 hover:bg-gray-700 text-gray-200"
              : "border-gray-300 hover:bg-gray-50 text-gray-700"
          }`}
        >
          <Icon.Heart />
          {likeInfo.liked ? "Liked" : "Like"}
          {likeInfo.count ? ` • ${likeInfo.count}` : ""}
        </button>

        <button
          onClick={toggleComment}
          className={`inline-flex items-center gap-2 px-3 py-1.5 rounded-lg border transition ${
            isDarkMode
              ? "border-gray-600 hover:bg-gray-700 text-gray-200"
              : "border-gray-300 hover:bg-gray-50 text-gray-700"
          }`}
        >
          <Icon.Message />
          {openComments ? "Hide comments" : "Comment"}
        </button>
      </div>

      {openComments && (
        <div className="mt-5">
          <CommentsSection postId={post._id || post.id} title="Comments" />
        </div>
      )}
    </div>
  );
}
